import { useState } from "react";


const GroupedTeamMembers = ({employees,selectedTeam, setTeam}) => {

    const [groupedEmployees, setGroupedData] = useState(groupTeamMembers());

    function groupTeamMembers() {
        var teams = [];

        var teamAMemebrs  = employees.filter((employee) => employee.teamName === 'TeamA');
        var teamA = {team:'TeamA', members:teamAMemebrs , collapsed: selectedTeam === 'TeamA'? false : true}
        teams.push(teamA);


        var teamBMemebrs  = employees.filter((employee) => employee.teamName === 'TeamB');
        var teamB = {team:'TeamB', members:teamBMemebrs , collapsed: selectedTeam === 'TeamB'? false : true}
        teams.push(teamB);


        var teamCMemebrs  = employees.filter((employee) => employee.teamName === 'TeamC');
        var teamC = {team:'TeamC', members:teamCMemebrs , collapsed: selectedTeam === 'TeamC'? false : true}
        teams.push(teamC);


        var teamDMemebrs  = employees.filter((employee) => employee.teamName === 'TeamD');
        var teamD = {team:'TeamD', members:teamDMemebrs , collapsed: selectedTeam === 'TeamD'? false : true}
        teams.push(teamD);

        return teams;

    }


    function handleTeamClick(event){
        var transformedGroupData = groupedEmployees.map((groupedData)=> groupedData.team === event.currentTarget.id 
                                                                        ? {...groupedData , collapsed:!groupedData.collapsed}
                                                                        : groupedData)

        setGroupedData(transformedGroupData);
        setTeam(event.currentTarget.id);
    }


    return (
        <main className="container">
            {
                groupedEmployees.map((item) => {
                    return (
                        <div key = {item.team} className = 'card mt-2' style={{cursor:'pointer'}}>
                            <h4 id = {item.team} className='card-header text-secondary bg-white' onClick={handleTeamClick}>
                                Team Name : {item.team}
                            </h4>
                            <div id ={'collapse_'+item.team} className={item.collapsed=== true?'collapse':''}>
                                <hr/>
                                    {
                                        item.members.map(member => {
                                            return(
                                                <div className="mt-2">
                                                    <h5 className="card-title mt-2">
                                                        <span className="text-dark">Full Name:{member.fullName}</span>
                                                    </h5>
                                                    <p>Designation : {member.designation}</p>
                                                </div>
                                            );
                                        })
                                    }

                            </div>
                        </div>
                    )
                }) 
            }
        </main>
    )

}

export default GroupedTeamMembers;
